import React from 'react';
import Card from './Card'; // Ensure the correct path for the Card component

const Cards = ({ courses }) => {
  console.log("Printing data");
  console.log(courses);

  function getCourses() {
    const allCourses = [];
    // Check if courses is not null and is an object
    if (courses && typeof courses === 'object') {
      Object.values(courses).forEach((ar) => {
        // Check if ar is an array before using forEach
        if (Array.isArray(ar)) {
          ar.forEach((courseData) => {
            allCourses.push(courseData);
          });
        } else {
          console.warn("Expected array but got:", ar);
        }
      });
    } else {
      console.warn("Courses data is not in the expected format:", courses);
    }
    return allCourses;
  }

  const allCourses = getCourses();

  return (
    <>
      {allCourses.length > 0 ? (
        allCourses.map((course) => (
          <Card key={course.id} course={course} /> // Pass course data to Card component
        ))
      ) : (
        <p>No courses available</p>
      )}
    </>
  );
};

export default Cards;
