import React, { useState } from 'react';
import { FcLike } from 'react-icons/fc'; // Default like icon

const Card = ({ course }) => {
  const [clicked, setClicked] = useState(false);

  function clickHandler() {
    setClicked(!clicked); // Toggle the clicked state
  }

  return (
    <div className='w-[300px] bg-bgDark rounded-md overflow-hidden bg-opacity-80'>
      {/* Image Section */}
      <div className='relative'>
        <img src={course.image.url} alt='Course' />
        {/* Like Button */}
        <div className='w-[40px] h-[40px] bg-white rounded-full absolute right-2 bottom-3 grid place-items-center'>
          <button onClick={clickHandler} className='flex justify-center items-center'>
            {/* Render FcLike with different colors based on the "clicked" state */}
            <FcLike
              fontSize='1.75rem'
              style={{ color: clicked ? 'red' : '#f5f5f5' }} // Red when liked, skinned white when unliked
            />
          </button>
        </div>
      </div>

      {/* Text Content Section */}
      <div className='p-4'>
        <p className='text-white font-bold'>{course.title}</p>
        <p className='my-2 text-white'>{course.description}</p>
      </div>
    </div>
  );
};

export default Card;
