import React, { useState } from 'react';
import { FcLike } from 'react-icons/fc'; // Default like icon
import { AiFillHeart } from 'react-icons/ai'; // Filled heart icon for "liked" state

const Card = ({ course }) => {
  const [clicked, setClicked] = useState(false);

  function clickHandler() {
    setClicked(!clicked); // Toggle the clicked state
    console.log("Clicked state:", !clicked); // Debug to confirm state change
  }

  return (
    <div className='w-[300px] bg-bgDark rounded-md overflow-hidden bg-opacity-80'>
      {/* Image Section */}
      <div className='relative'>
        <img src={course.image.url} alt='Course' />
        {/* Like Button */}
        <div className='w-[40px] h-[40px] bg-white rounded-full absolute right-2 bottom-3 grid place-items-center'>
          <button onClick={clickHandler} className='flex justify-center items-center'>
            {/* Conditional rendering of the heart icon */}
            {clicked ? (
              <AiFillHeart color='red' fontSize='1.75rem' /> // Filled red heart when clicked
            ) : (
              <FcLike fontSize='1.75rem' color='skin'/> // Default like icon when not clicked
            )}
          </button>
        </div>
      </div>

      {/* Text Content Section */}
      <div className='p-4'>
        <p className='text-white font-bold'>{course.title}</p>
        <p className='my-2 text-white'>{course.description}</p>
      </div>
    </div>
  );
};

export default Card;
